$(function(){var a=$("#javaScriptContent");var b=$(document);b.on("submit","#form",function(d){d.preventDefault();var e=$(this);var c=e.serializeArray();a.show();a.html('<div class="loading"><img src="images/loading.gif" alt="Lädt …" /></div>');$.ajax({url:e.attr("action"),type:"POST",data:c,dataType:"json",cache:false,success:function(g){var f=g;var h="";if(f.length>1){h="<h3>Umgewandelte Links:</h3>"}else{if(typeof f.length!=="undefined"){h="<h3>Umgewandelter Link:</h3>"}}$.each(f,function(i,j){if(j.link.length<=1){a.text("Keine URL angegeben.")}else{h=h+'<a href="'+j.link+'">'+j.title+'</a> <span class="light">&ndash; Referrer: '+j.refcode+"</span><br />";a.html(h)}})}})});$(".date").click(function(c){$(this).children("ul").animate({height:"toggle"})})});function autoGrow(a){if(a.scrollHeight>70){a.style.height="5px";a.style.height=(a.scrollHeight)+"px"}}function stripUrl(a){var b=a.replace("/([w]+://)/i","");if(b.length>60){var c=b.substr(0,45)+"&hellip;"+b.substr(-15);return c}else{return b}};